/**
 *  Unwraps a result _input_, returns the content of an `Some(T)`.
 *  If the value is an `None` then it calls `def` with its value.
 */
export function unwrapOrElseFromOption(input, recoverer) {
  if (input.ok) {
    var val = input.val;
    return val;
  }

  var fallback = recoverer();
  return fallback;
}