/**
 *  Unwraps a result _input_, returns the content of an `Ok(T)`.
 *  If the value is an `Err(E)` then it calls `def` with its value.
 */
export function unwrapOrElseFromResult(input, recoverer) {
  if (input.ok) {
    var val = input.val;
    return val;
  }

  var fallback = recoverer(input.err);
  return fallback;
}