import { isSome, createNone } from "./Option.mjs";
/**
 *  Return one of this pattern:
 *
 *  1. a=Some, b=None => _a_
 *  2. a=None, b=Some => _b_
 *  3. Others => `None`
 */

export function xorForOption(a, b) {
  var aIsSome = isSome(a);
  var bIsSome = isSome(b);

  if (aIsSome && !bIsSome) {
    return a;
  }

  if (!aIsSome && bIsSome) {
    return b;
  }

  return createNone();
}